        "id": "fiduciary-duty-001",
        "name": "Fiduciary Neutrality",
        "description": "AI must not recommend specific stocks or cryptocurrencies to avoid unlicensed financial advice liability.",
        "is_active": true,
        "severity": "CRITICAL"
    },
    {
        "id": "risk-transparency-002",
        "name": "Risk Transparency",
        "description": "Any discussion of investment must include a mandatory disclaimer that past performance does not guarantee future results.",
        "is_active": true,
        "severity": "HIGH"
    },
    {
        "id": "crypto-block-003",
        "name": "Crypto Recommendation Guard",
        "description": "Strict prohibition on promising 'risk-free' or 'guaranteed' returns on volatile assets like Bitcoin or ETH.",
        "is_active": true,
        "severity": "CRITICAL"
    }
]
